import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ItemDetail from '../../components/ItemDetail/ItemDetail';


function getItem(id){
    const myPromise = new Promise((resolve, reject) => {
        const data = [
            {
              id: 1,
              image:
                "data:image/jpeg;base64,",
              title: "ZAPATILLAS NIKE",
              descripcion: "ZAPATILLAS DE RUNNING PARA TU DÍA A DÍA HECHAS PARCIALMENTE CON MATERIALES RECICLADOS",
              categoria: "Zapatillas",
              price: "7300",
              stock: "4",
            },
            {
              id: 2,
              image: "https://static.nike.com/a/images/f_auto/dpr_1.0,cs_srgb/h_700,c_limit/4fa28619-6b5d-4bf3-b54d-4f6fa7cea4a2/paris-saint-germain.png",
              title: "CAMISETA PSG",
              descripcion: "ESTE PRODUCTO ESTÁ FABRICADO AL 100% CON FIBRAS DE POLIÉSTER RECICLADO.",
              categoria: "Camiseta",
              price: "15000",
              stock: "10",
            },
            {
              id: 3,
              image:
                "data:image/jpeg;base64,",
              title: "ZAPATILLAS DURAMO SL 2.0",
              descripcion: "ZAPATILLAS DE RUNNING PARA TU DÍA A DÍA HECHAS PARCIALMENTE CON MATERIALES RECICLADOS",
              categoria: "Zapatillas",
              price: "10000",
              stock: "5",
            },
            {
              id: 4,
              image:
                "data:image/jpeg;base64,",
              title: "CAMISETA REAL MADRID",
              descripcion: "UNA CAMISETA PARA HINCHAS DISEÑADA PARA CELEBRAR",
              categoria: "Camiseta",
              price: "15000",
              stock: "3",
            }];

            const item = data.filter(item => item.id == id);
        setTimeout(() => {
            resolve(item[0]);
        }, 2000)
    });
    return myPromise;

}

function ItemDetailContainer (){
    const [item, setItem] = useState({});
    const { id } = useParams
    useEffect(() => {
        console.log(id);
        getItem(id)
        .then(res => {
            setItem(res);
        })
        .catch(err =>{
            console.log(err);
            alert("Ocurrió un error");
        });
    }, []);

    return(
    <div>
        <ItemDetail item={item} />
    </div>
)
}


export default ItemDetailContainer
