import React, { useState } from "react";
import "./ItemCount.css";
import { Button } from "react-bootstrap";

function ItemCount(){
    const[count, setCount] = useState(1);
    return(
        
        <div className="container my-5">
            <div className="card text-center my-5">
                <div className="card-body">
                    <h1>Zapatillas Adidas</h1>
                        <div className="my-5">
                            <h2 className="my-5">{count}</h2>
                                <button onClick={()=> setCount(count + 1)} disabled={count>=5}>+</button>
                                <button className="restar" onClick={()=> setCount(count - 1)} disabled={count === 0}>-</button>
                                <br />
                                <button onClick={()=> setCount(count + 1)} disabled={count>=5}>Agregar al carrito</button>
                            
                        </div>
                </div>
            </div>
        </div>
    )
};



export default ItemCount
