import React, { useState } from "react";
import "./ItemCount.css";


const ItemCount =({ stock, initial })=>{
    const[count, setCount] = useState(initial);
    console.log("Soy el contador", count);
    const onIncrease =()=>{
        const newValue = count +1;
        if (newValue <= stock){
            setCount(newValue);
        }
    };
    const onDecrease =()=>{
        const newValue = count - 1;
        if(newValue >= initial){
            setCount(newValue);
        }
    };
    const onAdd =()=>{
        alert("Agregaste " + count + " productos al carrito")
    }
    return(
        <div className="d-flex flex-column">
            <div className="d-flex mt-3">
                <button variant="primary" onClick={onDecrease} className="px-3 mx-3" disabled={count <= 1 }>-</button>{" "}
                <h3 className="px-3 mx-3">{count}</h3>
                <button variant="primary" onClick={onIncrease} className="px-3 mx-3" disabled={count >=10}>+</button>{" "}
            </div>
                <button variant="danger" onClick={onAdd} className="mt-3">Agregar al Carrito</button>{" "}
        </div>
    )
};



export default ItemCount
