import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ItemDetail from '../../components/ItemDetail/ItemDetail';
import './ItemDetailContainer.css';

function getItem(id) {
    const myPromise = new Promise((resolve, reject) => {
        const productsList = [
            {
                id: 1,
                title: 'ZAPATILLAS ADIDAS SHOWTHEWAY',
                price: '$10040',
                stock: 7,
                imageUrl:"https://essential.vteximg.com.br/arquivos/ids/526744-454-423/261-3040_1.jpg?v=637815039563370000"
            },
            {
                id: 2,
                title: 'Superman',
                price: '$600',
                stock: 5,
                imageUrl: 'https://www.ecccomics.com/content/productos/10036/Superman_109_30_1a_cubierta_CORR.jpg'
            },
            {
                id: 3,
                title: 'Flash',
                price: '$350',
                stock: 5,
                imageUrl: 'https://www.ecccomics.com/content/productos/5447/Flash_28.jpg'
            }
        ];
        const item = productsList.filter(item => item.id === parseInt(id));
        setTimeout(() => {
            resolve(item[0]);
        }, 2000);
    });
    return myPromise;
}

function ItemDetailContainer() {
    const [item, setItem] = useState({});
    const { id } = useParams();

    useEffect(() => {
        getItem(id)
            .then(res => {
                setItem(res);
            })
            .catch(err => {
                console.log(err);
                alert('Ocurrio un error, revisar la consola!');
            });
    }, [id]);

    return (
        <div className='item-detail-container'>
            <ItemDetail item={item} />
        </div>
    )
}

export default ItemDetailContainer
