import React, { useState } from "react";
import "./ItemCount.css";


const ItemCount =({ stock, initial })=>{
    const[count, setCount] = useState(initial);
    
    const onIncrease =()=>{
        const newValue = count + 1;
        if (newValue <= stock){
            setCount(newValue);
        }
    };
    const onDecrease =()=>{
        const newValue = count - 1;
        if(newValue >= initial){
            setCount(newValue);
        }
    };
    const onAdd =()=>{
        alert("Agregaste " + count + " productos al carrito")
    }
    return(
        <div>
            <div>
                <h2>Zapatillas Adidas</h2>
            </div>
            <div>
                <h3 >{count}</h3>
            </div>
            <div>
                <button variant="primary" onClick={onDecrease} disabled={count <= 1 }>-</button>{" "}
                <button variant="primary" onClick={onIncrease} disabled={count >=10}>+</button>{" "}
            </div>
                
            
                <button variant="danger" onClick={onAdd}>Agregar al Carrito</button>{" "}
        </div>
    )
};



export default ItemCount
